import { useEffect, useState } from 'react';
import { GoogleOAuthProvider } from '@react-oauth/google';
import { io } from 'socket.io-client';
import { encryptMessage, decryptMessage } from './utils/common-utils';

// Components
import UserProvider from './context/UserProvider';
import AccountProvider from './context/AccountProvider';
import Loader from './components/loader/Loader';
const Messenger = lazy(() => import('./components/Messenger'));

function App() {
    const [socket, setSocket] = useState(null);

    const clientId = '357218936319-286l96mv0ceqcqtiedrfbtsn4um2ivsr.apps.googleusercontent.com';

    useEffect(() => {
        const socket = io('http://localhost:9000');
        setSocket(socket);

        return () => {
            socket.disconnect();
        };
    }, []);

    const sendMessage = (data) => {
        const { conversationId, text, receiverId } = data;
        const conversationKey = conversationKeys[conversationId];
        if (!conversationKey) {
            console.error('Conversation key not found');
            return;
        }
        const encryptedMessage = encryptMessage(text, conversationKey);
        const encryptedData = { ...data, text: encryptedMessage.encryptedData, iv: encryptedMessage.iv };
        socket.emit('sendMessage', encryptedData);
    };

    useEffect(() => {
        if (!socket) return;

        socket.on('getMessage', (data) => {
            const { conversationId, text, iv } = data;
            const conversationKey = conversationKeys[conversationId];
            if (!conversationKey) {
                console.error('Conversation key not found');
                return;
            }
            const decryptedText = decryptMessage(text, iv, conversationKey);
            // Process decrypted message
        });

        return () => {
            socket.off('getMessage');
        };
    }, [socket]);

    return (
        <GoogleOAuthProvider clientId={clientId}>
            <UserProvider>
                <AccountProvider>
                    <Suspense fallback={<Loader />}>
                        <Messenger sendMessage={sendMessage} />
                    </Suspense>
                </AccountProvider>
            </UserProvider>
        </GoogleOAuthProvider>
    );
}

export default App;
